import React, { useState } from 'react';
import './LoginScreen.css';
import axios from 'axios';

const LoginScreen = ({
  onRegisterClick,
  onForgotPassword,
  onForgotUsername,
  onLoginSuccess
}) => {
  const [formData, setFormData] = useState({
    username: '',
    accountNumber: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Logging in with:', formData);

    // ‚úÖ RegEx patterns for login validation
    const patterns = {
      username: /^[a-zA-Z0-9_]{4,20}$/, // Alphanumeric + underscore, 4‚Äì20 chars
      password: /^.{8,}$/               // At least 8 characters
    };

    // ‚úÖ Validate each field before sending
    for (const [field, pattern] of Object.entries(patterns)) {
      if (!pattern.test(formData[field])) {
        alert(`‚ùå Invalid ${field} format`);
        return;
      }
    }

    try {
      const response = await axios.post('http://localhost:5000/login', {
        username: formData.username,
        password: formData.password
      });

      alert(`‚úÖ Welcome back, ${response.data.fullName}!`);
      onLoginSuccess(response.data);
    } catch (error) {
      console.error('Login error:', error);
      if (error.response) {
        alert(`‚ùå ${error.response.data.error}`);
      } else {
        alert('‚ùå Network error. Please try again.');
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <div className="button-group">
            <button type="submit" className="signin-btn">Sign In</button>
            <button type="button" className="register-btn" onClick={onRegisterClick}>Register</button>
          </div>
          <div className="link-group">
            <button type="button" onClick={onForgotPassword}>Forgot password</button>
            <button type="button" onClick={onForgotUsername}>Forgot username</button>
          </div>
        </form>
        <div className="contact-link">
          üìû <a href="/contact">Contact Us</a>
        </div>
      </div>
    </div>
  );
};

export default LoginScreen;
