import React, { useState } from 'react';
import axios from 'axios';
import './LoginScreen.css'; // optional if you have styles

const LoginScreen = ({ onLoginSuccess, onRegisterClick, onForgotPassword, onForgotUsername }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('https://localhost:443/login', { username, password });
      // Pass the whole user object (including role) back to App.js
      onLoginSuccess(res.data);
    } catch (err) {
      console.error('Login error:', err);
      setError('Invalid login. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>SecurePay Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        /><br/>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br/>
        <button type="submit">Login</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div className="login-links">
        <button type="button" onClick={onRegisterClick}>Register</button>
        <button type="button" onClick={onForgotPassword}>Forgot Password</button>
        <button type="button" onClick={onForgotUsername}>Forgot Username</button>
      </div>
    </div>
  );
};

export default LoginScreen;
