import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  // ✅ Fetch all transactions on mount
  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const res = await axios.get('https://localhost:443/admin/transactions');
        setTransactions(res.data);
      } catch (err) {
        console.error('Error fetching transactions:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchTransactions();
  }, []);

  // ✅ Handle Verify
  const handleVerify = async (txnId) => {
    try {
      await axios.post('https://localhost:443/admin/verify', { txnId });
      setTransactions(transactions.map(txn =>
        txn.id === txnId ? { ...txn, status: 'verified' } : txn
      ));
    } catch (err) {
      console.error('Error verifying transaction:', err);
    }
  };

  // ✅ Handle Deny
  const handleDeny = async (txnId) => {
    try {
      await axios.post('https://localhost:443/admin/deny', { txnId });
      setTransactions(transactions.map(txn =>
        txn.id === txnId ? { ...txn, status: 'denied' } : txn
      ));
    } catch (err) {
      console.error('Error denying transaction:', err);
    }
  };

  if (loading) return <p>Loading transactions...</p>;

  return (
    <div className="admin-dashboard">
      <h2>Pending Transactions</h2>
      <table border="1" cellPadding="10" style={{ width: '100%', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>Customer</th>
            <th>Amount</th>
            <th>Currency</th>
            <th>SWIFT Code</th>
            <th>Status</th>
            <th>Reference</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {transactions.length === 0 ? (
            <tr>
              <td colSpan="7">No transactions found</td>
            </tr>
          ) : (
            transactions.map((txn) => (
              <tr key={txn.id}>
                <td>{txn.customer}</td>
                <td>{txn.amount}</td>
                <td>{txn.currency}</td>
                <td>{txn.swiftCode}</td>
                <td>{txn.status}</td>
                <td>{txn.reference}</td>
                <td>
                  {txn.status === 'pending' ? (
                    <>
                      <button
                        style={{ marginRight: '10px', background: 'green', color: 'white' }}
                        onClick={() => handleVerify(txn.id)}
                      >
                        Verify
                      </button>
                      <button
                        style={{ background: 'red', color: 'white' }}
                        onClick={() => handleDeny(txn.id)}
                      >
                        Deny
                      </button>
                    </>
                  ) : (
                    <span>{txn.status.toUpperCase()}</span>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
